mapper.insert:

	select seq_bno.nextval
	from dual

insert into board values (
	seq_bno.nextval, #{btitle}, #{bcontent}, sysdate, #{mid},
	0, #{battachoname}, #{battachsname}, #{battachtype}, #{battachdata}
)

mapper.select:

select bno, btitle, bdate, mid, bhitcount
from (
	select rownum as rnum, bno, btitle, bdate, mid, bhitcount
	from (
		select bno, btitle, bdate, mid, bhitcount
		from board
		order by bno desc
	)
	where rownum <= #{endRowNo}
) where rnum >= #{startRowNo}

select bno, btitle, bcontent, mid, bdate, bhitcount, battachoname
from board
where bno = #{bno}

select battachoname, battachtype, battachdata
from board
where bno = #{bno}

mapper.update:
update 
	board
set 
	btitle=#{btitle}, 
	bcontent=#{bcontent}

		,
		battachoname=#{battachoname},
		battachtype=#{battachtype},
		battachdata=#{battachdata}

where bno=#{bno}
