mapper.insert:

	select seq_bno.nextval
	from dual

insert into board values (
	seq_bno.nextval, #{btitle}, #{bcontent}, sysdate, #{mid},
	0, #{battachoname}, #{battachsname}, #{battachtype}, #{battachdata}
)

mapper.select:

select bno, btitle, bdate, mid, bhitcount
from (
	select rownum as rnum, bno, btitle, bdate, mid, bhitcount
	from (
		select bno, btitle, bdate, mid, bhitcount
		from board
		order by bno desc
	)
	where rownum <= #{endRowNo}
) where rnum >= #{startRowNo}
